import React from 'react'


function landingPage(props) {

    var themonth = 1;
renderCal(themonth);

$('.minusmonth').click(function(){
  themonth += -1;
  renderCal(themonth);
});

$('.addmonth').click(function(){
  themonth += 1;
  renderCal(themonth);
});

function renderCal(themonth){
$('.calendar li').remove();
$('.calendar ul').append('<li>Mo</li><li>Tu</li><li>We</li><li>Th</li><li>Fr</li><li>Sa</li> <li>Su</li>');
var d = new Date(),
  currentMonth = d.getMonth()+themonth, // get this month
  days = numDays(currentMonth,d.getYear()), // get number of days in the month
  fDay = firstDay(currentMonth,d.getYear())-1, // find what day of the week the 1st lands on
  months = ['January','February','March','April','May','June','July','August','September','October','November','December']; // month names

$('.calendar p.monthname').text(months[currentMonth-1]); // add month name to calendar

for (var i=0;i<fDay-1;i++) { // place the first day of the month in the correct position
  $('<li>&nbsp;</li>').appendTo('.calendar ul');
}

for (var i = 1;i<=days;i++) { // write out the days
  $('<li>'+i+'</li>').appendTo('.calendar ul');
}

function firstDay(month,year) {
  return new Date(year,month,1).getDay();
}

function numDays(month,year) {
  return new Date(year,month,0).getDate();
}

var clicker = 0;
var min = 0;
var max = 0;

$('.calendar li').click(function(){ // toggle selected dates
  if(clicker==0){
    clicker=1;
    $('.calendar li').removeClass('red');
    $(this).addClass('red');
    min = $(this).text();
  } else {
    clicker=0;
    $(this).addClass('red');
    $('.calendar li.red').each(function(){
      max = $(this).text();
    });
    for(i=parseInt(min);i<parseInt(max);i++){
      $('.calendar li:nth-of-type('+(i+7+fDay-1)+')').addClass('red'); 
    }
  }
});
}



    return (
        <div className="landing-div">


            <div className="center-orange-divs">
                <div className="small-orange-rectangle">
                    <h2>GO GREENER!</h2>
                </div>

                <div className="big-orange-rectangle">
                    <h1>Save More On Eco-Friendly Vehicles</h1>
                </div>
            </div>

            <div className="FAVPNG_leaves_rkB7hhB5"></div>

            <div className="blue-div">
                <form action="">

                    <input type="text" className="pick-up-searchbar" placeholder="Enter Your Pickup Location"/>

                    <div className="location-checkbox-div">
                        <input type="checkbox" /> 
                        <p className="location-checkbox">Returning to a Different Location?</p>
                    </div>

                    <div className="landing-date-time">
                        <input type="text" className="select-dates" placeholder="Select My Dates" />

                        <div className="calendar">

                            <button className="animate"> Select Dates </button>

                            <ul classname="animate">


                                <h1 className="center"></h1>

                                <div className="calendar">
                                    <div classname="group">
                                        <p className="left pointer minusmonth"></p>
                                        <p className="left monthname center pointer"></p>
                                        <p className="right pointer addmonth"></p>
                                    </div>

                                    <ul className="group">
                                        <li>Mo</li>
                                        <li>Tu</li>
                                        <li>We</li>
                                        <li>Th</li>
                                        <li>Fr</li>
                                        <li>Sa</li>
                                        <li>Su</li>
                                    </ul>
                                </div>

                            </ul>

                        </div>

                        <input type="text" className="select-time" placeholder="Pickup Time" />
                        <input type="text" className="select-time" placeholder="Dropoff Time" />
                    </div>

                    <div className="car-type-div">

                        <div className="vehicle-dropdown">
                            <button className="drop-vehicle-button">Select Vehicle Type</button>
                                <div className="vehicle-dropdown-content">
                                    <button onClick={props.setVehicleType} value="Economy" className="car-type-buttons">Economy</button>
                                    <button onClick={props.setVehicleType} value="Compact" className="car-type-buttons">Compact</button>
                                </div>
                        </div>

                        <input type="checkbox" />
                        <p className="location-checkbox">Under 25?</p>
                    </div>

                    <div className="car-select-button-div">
                        <button className="car-select-button" onSubmit={props.setLocation}> <p className="car-select-button-text">Select my car</p> </button>
                    </div>

                </form>
            </div>




        </div>
    )
}

export default landingPage
