import React, { useState } from 'react'
import './App.css'
import LandingPage from './components/landingPage'
import { Route, withRouter } from 'react-router-dom'
import SelectACar from './components/routes/SelectACar'
import Nav from './components/Nav';

function App() {
  const [pickUpLocation, setPickUpLocation] = useState('')
  const [vehicleType, setVehicleType] = useState('')

  console.log(vehicleType)

  const handlePickUpLocationClick = (e) => {
    e.preventDefault()
    let location = (e.target.value)
    setPickUpLocation(location)
}

  const handleVehicleTypeClick = (e) => {
    // e.preventDefault()
    setVehicleType('Economy')
}

  return (
    <React.Fragment>
      <div className="App">
        {/* <Nav /> */}
        <Route exact path="/" 
            render={ (routeProps) => (<LandingPage {...routeProps} setLocation={handlePickUpLocationClick} setVehicleType={handleVehicleTypeClick}/>)} />
        <Route exact path="/carselect" component={SelectACar} />
      </div>
    </React.Fragment>
  );
}


export default withRouter(App)
